var fs 		= require('fs');
var path 	= require('path');

function ls(dir, ext, callback){
	fs.readdir(dir, function(err, list){
		if(err) 
			return callback(err);

		ext 	= '.' + ext;
		list = list.filter(function(file){
			return path.extname( file ) === ext;
		});
		callback(null, list);		
	});
};

module.exports = ls;